var reverseList = function(head) {
    if(!head || !head.next){
        return head;
    }

    // 当前节点
    let current = head;
    // 当前节点的前一个节点
    let prev = null;
    while(current){
        const next = current.next;
        // 将当前节点的下一个节点指向上一个节点
        current.next = prev;
        // 将当前节点作为下一个节点
        prev = current;
        // 将下一个节点作为当前节点
        current = next;
    }
    // 将反转后的节点返回
