var reverseKGroup = function (head, k) {
    if (head === null || head.next === null) {
        return head;
    }
    let current = head;
    let index = 1;
    let tempList = null;
    let lastNode = null;
    let firstNode = null;
    while (current) {
        if (index === 1) {
            tempList = current;
        }
        let next = current.next;
        if (index === k) {
            // 开始反转 切断链表
            current.next = null;
            let returncurrent = tempList;
            let prev = null;
            // 需要把prev的当成current的下个节点
            while (returncurrent) {
                const next = returncurrent.next;
                // 把前一个节点挪到当前节点后面
                returncurrent.next = prev;
                // 把当前节点当成前一个节点
                prev = returncurrent;
                // 把下个节点作为当前节点
                returncurrent = next;
            }
            if(lastNode !== null){
                lastNode.next = prev;
            }
            if (firstNode === null) {
                firstNode = prev;
            }
            // 反转完的节点为prev
            index = 0;
            tempList = null;
            // lastNode指向最后一个节点
            while (prev.next) {
                prev = prev.next;
            }
            lastNode = prev;
        }
        index++;
        current = next;
    }
    if (tempList) {
        lastNode.next = tempList;
    }

    return firstNode;

};

