```js
const rollup = require('rollup');
// 
const inputOptions = {...};
const outputOptions = {...};

async function build() {
  //  bundle
  const bundle = await rollup.rollup(inputOptions);

  console.log(bundle.watchFiles); //  bundle 

  // 
  //  bundle 
  const { output } = await bundle.generate(outputOptions);

  for (const chunkOrAsset of output) {
    if (chunkOrAsset.type === 'asset') {
      // assets
      // {
      //   fileName: string,              // asset 
      //   source: string | Uint8Array    // asset 
      //   type: 'asset'                  //  asset
      // }
      console.log('Asset', chunkOrAsset);
    } else {
      // chunks, 
      // {
      //   code: string,                  // JS
      //   dynamicImports: string[],      // chunk 
      //   exports: string[],             // 
      //   facadeModuleId: string | null, // chunkID
      //   fileName: string,              // chunk
      //   imports: string[],             // chunk 
