/**
 * Promise
 */
const StatusType = {
    PENDING: 'pending',
    FULFILLED: 'fulfilled',
    REJECTED: 'rejected'
}
/**
 * Promise
 * @author: Qborfy
 */
class MyPromise {

    /**
     * 
     * @param {*} executor resolvereject
     */
    constructor(executor) {
        // pending
        this.status = StatusType.PENDING;
        // 
        this.value = undefined;
        // 
        this.reason = undefined;
        // 
        this.onFulfilledCallbacks = [];
        // 
        this.onRejectedCallbacks = [];

        // 
        const resolve = (value) => {
            // pendingfulfilledrejected
            if (this.status === StatusType.PENDING) {
                this.status = StatusType.FULFILLED;
                this.value = value;
                //  queueMicrotask()
                this.onFulfilledCallbacks.forEach(fn => {
                    queueMicrotask(fn(this.value));
                });
            }
        }

        // 
        const reject = (reason) => {
            // pendingfulfilledrejected
            if (this.status === StatusType.PENDING) {
                this.status = StatusType.REJECTED;
                this.reason = reason;
                //  queueMicrotask()
                this.onRejectedCallbacks.forEach(fn => {
                    queueMicrotask(fn(this.reason));
                });
            }
        }

        try {
            // executor
            executor(resolve, reject);
        } catch (e) {
            // executorreject
            reject(e);
        }
    }

    /**
     * thenpromiseresolvereject
     * @param {*} onFulfilled 
     * @param {*} onRejected 
     */
    then(onFulfilled, onRejected) {
        // onFulfilledonRejected
        onFulfilled = typeof onFulfilled === 'function' ? onFulfilled : value => value;
        onRejected = typeof onRejected === 'function' ? onRejected : err => { throw err };
        // promisepromisepromisepromise
        function resolvePromise(result, resolve, reject) {
            if (result instanceof MyPromise) {
                result.then(resolve, reject);
            } else {
                resolve(result);
            }
        }
        const newPromise = new MyPromise((resolve, reject) => {
            // onFulfilled
            if (this.status === StatusType.FULFILLED) {
                const result = onFulfilled(this.value);
                resolvePromise(result, resolve, reject);
            }
            // onRejected
            if (this.status === StatusType.REJECTED) {
                const result = onRejected(this.reason);
                resolvePromise(result, resolve, reject);
            }

            // pendingonFulfilledonRejected
            if (this.status === StatusType.PENDING) {
                // resolvereject
                this.onFulfilledCallbacks.push((value) => {
                    const result = onFulfilled(value)
                    resolvePromise(result, resolve, reject)
                });
                this.onRejectedCallbacks.push(() => {
                    const result = onRejected(reason)
                    resolvePromise(result, resolve, reject)
                })
            }
        })

        return newPromise;
    }


    /**
     * catchpromise
     * @param {*} onRejected 
     */
    catch(onRejected) {
        return this.then(null, onRejected);
    }

    /**
     * finallypromise
     * @param {*} onFinally 
     * @returns 
     */
    finally(onFinally) {
        // valuereason
        return this.then((value)=>{
            onFinally()
            return value;
        }, (reason)=>{
            onFinally()
            return reason;
        })
    }

